import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../../context/AuthContext';
import './UserDashboard.css';

const UserDashboard = () => {
    const [clients, setClients] = useState([]);
    const [loading, setLoading] = useState(true);
    const [searchTerm, setSearchTerm] = useState('');
    const { user } = useContext(AuthContext);

    useEffect(() => {
        // Function to fetch client data
        const fetchClients = async () => {
            try {
                // In a real app, this would be an API call
                // For now, we'll use sample data
                const sampleClients = [
                    {
                        id: 1,
                        name: 'Raj Enterprises',
                        email: 'contact@rajenterprises.com',
                        phone: '+91 98765 43210',
                        clientType: 'Pvt Ltd',
                        address: 'Mumbai, Maharashtra'
                    },
                    {
                        id: 2,
                        name: 'Sharma & Sons',
                        email: 'info@sharmasons.com',
                        phone: '+91 87654 32109',
                        clientType: 'Partnership',
                        address: 'Delhi, NCR'
                    },
                    {
                        id: 3,
                        name: 'Patel Trading Co.',
                        email: 'contact@pateltrading.com',
                        phone: '+91 76543 21098',
                        clientType: 'Individual',
                        address: 'Ahmedabad, Gujarat'
                    }
                ];

                setClients(sampleClients);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching clients:', error);
                setLoading(false);
            }
        };

        fetchClients();
    }, []);

    // Filter clients based on search term
    const filteredClients = clients.filter(client => 
        client.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        client.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
        client.phone.includes(searchTerm)
    );

    return (
        <div className="user-dashboard">
            <div className="welcome-section">
                <h1>Welcome, {user?.username || 'User'}</h1>
                <p>This is your dashboard for managing client information. Use the search below to find specific clients or browse the list.</p>
            </div>

            <div className="client-search">
                <input 
                    type="text"
                    placeholder="Search clients by name, email, or phone..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
            </div>

            {loading ? (
                <p>Loading clients...</p>
            ) : (
                <div className="client-list">
                    <h2>Your Clients ({filteredClients.length})</h2>

                    {filteredClients.length === 0 ? (
                        <p>No clients found matching your search.</p>
                    ) : (
                        <div className="client-grid">
                            {filteredClients.map(client => (
                                <div key={client.id} className="client-card">
                                    <div className="client-name">{client.name}</div>
                                    <div className="client-type">{client.clientType}</div>
                                    <div className="client-details">
                                        <div className="client-detail">📧 {client.email}</div>
                                        <div className="client-detail">📞 {client.phone}</div>
                                        <div className="client-detail">📍 {client.address}</div>
                                    </div>
                                    <div className="client-actions">
                                        <button className="action-button view-button">View Details</button>
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            )}
        </div>
    );
};

export default UserDashboard;
