import React from 'react';
import { useEffect, useState } from 'react';
import { getUsers, deleteUser, getUserPassword } from '../../services/api';
import UserCreate from '../User/UserCreate';
import UserForm from '../User/UserForm';
import './AdminDashboard.css';

const AdminDashboard = () => {
    const [users, setUsers] = useState([]);
    const [clients, setClients] = useState([]);
    const [stats, setStats] = useState({
        totalUsers: 0,
        totalClients: 0,
        activeClients: 0
    });
    const [isAddingUser, setIsAddingUser] = useState(false);
    const [editingUser, setEditingUser] = useState(null);
    const [errorMessage, setErrorMessage] = useState('');
    const [passwordView, setPasswordView] = useState({
        userId: null,
        hashedPassword: '',
        isVisible: false
    });

    useEffect(() => {
        fetchUsers();
        fetchStats();
    }, []);

    const fetchUsers = async () => {
        try {
            const response = await getUsers();
            setUsers(response || []);
            setStats(prevStats => ({
                ...prevStats,
                totalUsers: response ? response.length : 0
            }));
        } catch (error) {
            console.error('Error fetching users:', error);
            setErrorMessage('Failed to fetch users. Please try again.');
        }
    };

    const fetchStats = async () => {
        try {
            // In a real app, you would call an API endpoint to get dashboard stats
            // For now, we'll use sample data
            setStats({
                totalUsers: users.length,
                totalClients: 15,
                activeClients: 12
            });
        } catch (error) {
            console.error('Error fetching stats:', error);
        }
    };

    const handleAddUser = () => {
        setIsAddingUser(true);
        setEditingUser(null);
    };

    const handleEditUser = (user) => {
        setEditingUser(user);
        setIsAddingUser(false);
    };

    const handleUserSaved = () => {
        setIsAddingUser(false);
        setEditingUser(null);
        fetchUsers();
    };

    const handleCancelUserForm = () => {
        setIsAddingUser(false);
        setEditingUser(null);
    };

    const handleDeleteUser = async (userId) => {
        if (window.confirm("Are you sure you want to delete this user?")) {
            try {
                await deleteUser(userId);
                fetchUsers();
            } catch (error) {
                console.error('Error deleting user:', error);
                setErrorMessage('Failed to delete user. Please try again.');
            }
        }
    };

    return (
        <div className="admin-dashboard">
            <h1>Admin Dashboard</h1>

            {errorMessage && (
                <div className="error-message">
                    {errorMessage}
                    <button onClick={() => setErrorMessage('')} className="dismiss-button">×</button>
                </div>
            )}

            <div className="stats-cards">
                <div className="stat-card">
                    <h3>Total Users</h3>
                    <div className="stat-value">{stats.totalUsers}</div>
                </div>
                <div className="stat-card">
                    <h3>Total Clients</h3>
                    <div className="stat-value">{stats.totalClients}</div>
                </div>
                <div className="stat-card">
                    <h3>Active Clients</h3>
                    <div className="stat-value">{stats.activeClients}</div>
                </div>
            </div>

            {isAddingUser ? (
                <div className="card-section">
                    <div className="card-section-header">
                        <h2>Create New User</h2>
                    </div>
                    <div className="card-section-content">
                        <UserCreate onSave={handleUserSaved} onCancel={handleCancelUserForm} />
                    </div>
                </div>
            ) : editingUser ? (
                <div className="card-section">
                    <div className="card-section-header">
                        <h2>Edit User</h2>
                    </div>
                    <div className="card-section-content">
                        <UserForm 
                            user={editingUser} 
                            onSave={handleUserSaved} 
                            onCancel={handleCancelUserForm} 
                        />
                    </div>
                </div>
            ) : (
                <div className="card-section">
                    <div className="card-section-header">
                        <h2>Registered Users</h2>
                        <button className="add-user-button" onClick={handleAddUser}>
                            <i className="fas fa-plus"></i> Add User
                        </button>
                    </div>
                    <div className="card-section-content">
                        <table className="user-table">
                            <thead>
                                <tr>
                                    <th>Username</th>
                                    <th>Role</th>
                                    <th>Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {users.length > 0 ? (
                                    users.map(user => (
                                        <tr key={user._id}>
                                            <td>{user.username}</td>
                                            <td>
                                                <span className={`user-role ${user.role}`}>
                                                    {user.role}
                                                </span>
                                            </td>
                                            <td>
                                                <button 
                                                    className="action-button edit"
                                                    onClick={() => handleEditUser(user)}
                                                >
                                                    Edit
                                                </button>
                                                <button 
                                                    className="action-button delete"
                                                    onClick={() => handleDeleteUser(user._id)}
                                                >
                                                    Delete
                                                </button>
                                            </td>
                                        </tr>
                                    ))
                                ) : (
                                    <tr>
                                        <td colSpan="3" style={{ textAlign: 'center' }}>No users found</td>
                                    </tr>
                                )}
                            </tbody>
                        </table>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AdminDashboard;
