import React from 'react';
import { useEffect, useState } from 'react';
import { getUsers } from '../../services/api';
import './AdminDashboard.css';

const AdminDashboard = () => {
    const [users, setUsers] = useState([]);
    const [stats, setStats] = useState({
        totalUsers: 0,
        totalClients: 0,
        activeClients: 0
    });

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const response = await getUsers();
                setUsers(response.data || []);
                setStats(prevStats => ({
                    ...prevStats,
                    totalUsers: response.data ? response.data.length : 0
                }));
            } catch (error) {
                console.error('Error fetching users:', error);
            }
        };

        fetchUsers();
    }, []);

    const handleAddUser = () => {
        // Logic to navigate to user creation page or open modal
        console.log('Add user clicked');
    };

    return (
        <div className="admin-dashboard">
            <h1>Admin Dashboard</h1>

            <div className="stats-cards">
                <div className="stat-card">
                    <h3>Total Users</h3>
                    <div className="stat-value">{stats.totalUsers}</div>
                </div>
                <div className="stat-card">
                    <h3>Total Clients</h3>
                    <div className="stat-value">{stats.totalClients}</div>
                </div>
                <div className="stat-card">
                    <h3>Active Clients</h3>
                    <div className="stat-value">{stats.activeClients}</div>
                </div>
            </div>

            <div className="card-section">
                <div className="card-section-header">
                    <h2>Registered Users</h2>
                    <button className="add-user-button" onClick={handleAddUser}>
                        <i className="fas fa-plus"></i> Add User
                    </button>
                </div>
                <div className="card-section-content">
                    <table className="user-table">
                        <thead>
                            <tr>
                                <th>Username</th>
                                <th>Role</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {users.length > 0 ? (
                                users.map(user => (
                                    <tr key={user._id}>
                                        <td>{user.username}</td>
                                        <td>
                                            <span className={`user-role ${user.role}`}>
                                                {user.role}
                                            </span>
                                        </td>
                                        <td>
                                            <button className="action-button edit">Edit</button>
                                            <button className="action-button delete">Delete</button>
                                        </td>
                                    </tr>
                                ))
                            ) : (
                                <tr>
                                    <td colSpan="3" style={{ textAlign: 'center' }}>No users found</td>
                                </tr>
                            )}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
};

export default AdminDashboard;
